CREATE EXTENSION IF NOT EXISTS pgcrypto;
CREATE TABLE "public"."reprint"("id" uuid NOT NULL DEFAULT gen_random_uuid(), "printed_at" timestamptz NOT NULL, "batch" text NOT NULL, "userid" uuid NOT NULL, "distributorid" uuid NOT NULL, "voucherid" uuid NOT NULL, PRIMARY KEY ("id") , FOREIGN KEY ("userid") REFERENCES "public"."user"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("distributorid") REFERENCES "public"."distributor"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("voucherid") REFERENCES "public"."voucher"("id") ON UPDATE restrict ON DELETE restrict);
