CREATE TABLE "archive"."avoucher"("id" uuid NOT NULL, "created_at" timestamptz NOT NULL, "serial" text NOT NULL, "voucher" text NOT NULL, "expiration_date" timestamptz NOT NULL, "denominationid" uuid NOT NULL, "is_active" boolean NOT NULL, "printed_at" timestamptz NOT NULL, "userid" uuid NOT NULL, "distributorid" uuid NOT NULL, "sale_batch" text NOT NULL, "voucher_batch" text NOT NULL, "is_suspended" boolean NOT NULL, "buyer" text NOT NULL, "archived_at" timestamptz NOT NULL DEFAULT now(), PRIMARY KEY ("id") , FOREIGN KEY ("denominationid") REFERENCES "public"."denomination"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("distributorid") REFERENCES "public"."distributor"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("userid") REFERENCES "public"."user"("id") ON UPDATE restrict ON DELETE restrict, UNIQUE ("serial"));
