CREATE EXTENSION IF NOT EXISTS pgcrypto;
CREATE TABLE "public"."dist_balance_log"("id" uuid NOT NULL DEFAULT gen_random_uuid(), "created_at" timestamptz NOT NULL DEFAULT now(), "distributorid" uuid NOT NULL, "amount" numeric NOT NULL, "old_balance" numeric NOT NULL, "new_balance" numeric NOT NULL, "transferid" uuid, "topupid" uuid, PRIMARY KEY ("id") , FOREIGN KEY ("distributorid") REFERENCES "public"."distributor"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("topupid") REFERENCES "public"."dtopup"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("transferid") REFERENCES "public"."transfer"("id") ON UPDATE restrict ON DELETE restrict);
