CREATE TABLE "public"."ccd_balance_log" ("id" uuid NOT NULL DEFAULT gen_random_uuid(), "created_at" timestamptz NOT NULL DEFAULT now(), "amount" numeric NOT NULL, "ccdid" uuid NOT NULL, "old_balance" numeric NOT NULL, "new_balance" numeric NOT NULL, "loadid" uuid, "reversionid" uuid, "timestamp" text NOT NULL, PRIMARY KEY ("id") , FOREIGN KEY ("ccdid") REFERENCES "public"."ccd"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("loadid") REFERENCES "public"."load"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("reversionid") REFERENCES "public"."reversion"("id") ON UPDATE restrict ON DELETE restrict);
CREATE EXTENSION IF NOT EXISTS pgcrypto;
